package com.zjw.apps3pluspro.adapter;import android.annotation.SuppressLint;import android.content.Context;import android.text.TextUtils;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.TextView;import com.lidroid.xutils.BitmapUtils;import com.zjw.apps3pluspro.R;import com.zjw.apps3pluspro.application.BaseApplication;import com.zjw.apps3pluspro.network.javabean.ThemeBean;import com.zjw.apps3pluspro.sharedpreferences.BleDeviceTools;import java.util.ArrayList;import java.util.List;/** * 主题历史列表适配器 */public class ClockDialMarketListAdapter extends BaseAdapter {    static class ViewHolder {        ImageView list_my_theme_img;        TextView list_my_theme_title;        ImageView ivCover;    }    private Context mContext;    private int UiType = 1;    private BleDeviceTools mBleDeviceTools = BaseApplication.getBleDeviceTools();    private List<ThemeBean.DataBean.ListBean> myThemeData;    private LayoutInflater mInflator;    public ClockDialMarketListAdapter(Context context, int ui_type) {        super();        this.mInflator = LayoutInflater.from(context);        myThemeData = new ArrayList<>();        mContext = context;        UiType = ui_type;    }    public ThemeBean.DataBean.ListBean getDevice(int position) {        if (myThemeData != null && myThemeData.size() > position) {            return myThemeData.get(position);        } else {            return null;        }    }    public void RemoveDevice(int position) {    }    public void setDeviceList(List<ThemeBean.DataBean.ListBean> my_theme_list) {        myThemeData = my_theme_list;    }    public void RemoveAllDevice() {        myThemeData.removeAll(myThemeData);    }    public void clear() {        myThemeData.clear();    }    @Override    public int getCount() {        return myThemeData.size();    }    @Override    public Object getItem(int i) {        return myThemeData.get(i);    }    @Override    public long getItemId(int i) {        return i;    }    @SuppressLint("ResourceAsColor")    @Override    public View getView(final int position, View view, ViewGroup viewGroup) {        ViewHolder viewHolder;        if (view == null) {            if (UiType == 1) {                view = mInflator.inflate(R.layout.listitem_clock_dial_market_1, null);            } else {                view = mInflator.inflate(R.layout.listitem_clock_dial_market_2, null);            }            viewHolder = new ViewHolder();            viewHolder.list_my_theme_img = (ImageView) view.findViewById(R.id.list_my_theme_img);            viewHolder.list_my_theme_title = (TextView) view.findViewById(R.id.list_my_theme_title);            viewHolder.ivCover = (ImageView) view.findViewById(R.id.ivCover);            view.setTag(viewHolder);        } else {            viewHolder = (ViewHolder) view.getTag();        }        ThemeBean.DataBean.ListBean my_theme_data = myThemeData.get(position);        if (!TextUtils.isEmpty(my_theme_data.getThemeImgUrl())) {            new BitmapUtils(mContext).display(viewHolder.list_my_theme_img,                    my_theme_data.getThemeImgUrl());        }        viewHolder.list_my_theme_title.setText(my_theme_data.getAuthorName());        if (UiType == 2) {            if (mBleDeviceTools.get_device_theme_shape() == 2) {                viewHolder.ivCover.setVisibility(View.VISIBLE);            } else {                viewHolder.ivCover.setVisibility(View.GONE);            }        }        return view;    }}